        content = content.replace(
                      'cam.status === "active"',
                      'cam.is_active'
        )
        content = content.replace(
                      'cam.status === "error"',
                      'false'
        )
        content = content.replace(
                      '{cam.status === "active" ? "Live" : cam.status === "error" ? "Error" : "Offline"}',
                      '{cam.is_active ? "Live" : "Offline"}'
        )
